import { Component } from "react";
import styles from './SkillsContainer.module.css';
import './SkillsContainer.css';

export default class SkillsContainer extends Component {
    constructor() {
        super();    
    }


    fadeSlide() {
        this.sideLeft = document.getElementById("side-left");
        this.sideRight  = document.getElementById("side-right");

        this.sideLeft.animate({'left': '-100%'}, 500).onfinish = () => {
            this.sideLeft.style.left = '-100%';
            this.sideLeft.animate({'left': '-25%'}, 500).onfinish = () => {
                this.sideLeft.style.left = '-25%';
            }
        }

        this.sideRight.animate({'right': '-100%'}, 500).onfinish = () => {
            this.sideRight.style.right = '-100%';
            this.sideRight.animate({'right': '-25%'}, 500).onfinish = () => {
                this.sideRight.style.right = '-25%';
            }
        }
     }

    render() {
        return (
            <div className={styles.mainContainer}>
                <div id="side-left" className={styles.sideLeftContainer}></div>
                <div className={styles.centerContainer}>
                    <div id="first-img-slide" onClick={() => {this.fadeSlide()}} className={styles.centreSubContainer}>
                        <h3>My professional work with <strong>ASP.NET Core MVC</strong> mainly revolves around managing the database communication between the companies' database and the backend.
                                This entails mostly managing queries, both hardcoded queries and <strong>Entity Framework</strong>. When it comes my work in my leisure time I often build ASP.NET Core MVC Projects from scratch
                                where the frontend is what I lay most of my focus on. Although I do also, from time to time, hook up the backend to a local database.
                        </h3>
                    </div>
                    <div id="second-img-slide" onClick={() => { this.shiftSlide("second"); }}>
                    <h3>The Azovstal Steelworks</h3>
                    </div>
                <div id="third-img-slide" onClick={() => { this.shiftSlide("third"); }}>
                    <h3>The Devastation in Mariupol</h3>
                </div>
                <div id="fourth-img-slide" onClick={() => { this.shiftSlide("fourth"); }}>
                    <h3>The Seizure of Crimea</h3>
                </div>
                </div>
                <div id="side-right" className={styles.sideRightContainer}></div>
            </div>
        );
    }
}
