import { Component } from "react";
import styles from './SkillsContainer.module.css';
import './SkillsContainer.css';

export default class SkillsContainer extends Component {
    constructor() {
        super();
    }

    fadeSlide() {

        sideLeft.animate({'left': '-100%'}, 500).onfinish = () => {
            sideLeft.style.left = '-100%';
            sideLeft.animate({'left': '-25%'}, 500).onfinish = () => {
                sideLeft.style.left = '-25%';
            }
        }

        sideRight.animate({'right': '-100%'}, 500).onfinish = () => {
            sideRight.style.right = '-100%';
            sideRight.animate({'right': '-25%'}, 500).onfinish = () => {
                sideRight.style.right = '-25%';
            }
        }
     }

    render() {
        return (
            <div className={styles.mainContainer}>
                <div id="side-left" className={styles.sideLeftContainer}></div>
                <div className={styles.centerContainer}>
                    <div onClick={() => {this.fadeSlide()}} className={styles.centreSubContainer}>
                        <h3>Test1</h3>
                    </div>
                </div>
                <div id="side-right" className={styles.sideRightContainer}></div>
            </div>
        );
    }
}
